                "api_gateway": {
                    "type": "tosca.nodes.Container.Application.Docker",
                    "image": "swarmchestrate/api-gateway:v2.1.0",
                    "ports": ["8080:8080"],
                    "environment": {
                        "DATABASE_URL": "postgresql://dbuser:dbpass@postgres_db:5432/appdb",
                        "REDIS_URL": "redis://cache:6379",
                        "JWT_SECRET": "encrypted_secret_placeholder"
                    },
                    "endpoint_protocol": "http",
                    "endpoint_port": 8080,
                    "endpoint_path": "/api",
                    "dependencies": ["postgres_db", "redis_cache"],
                    "host": "backend_container_runtime"
                },
                "postgres_db": {
                    "type": "tosca.nodes.Database.PostgreSQL",
                    "name": "appdb",
                    "port": 5432,
                    "user": "dbuser",
                    "password": "encrypted_password_placeholder",
                    "endpoint_protocol": "postgresql",
                    "endpoint_port": 5432,
                    "host": "database_container_runtime"
                },
                "redis_cache": {
                    "type": "tosca.nodes.Database.Redis",
                    "port": 6379,
                    "max_memory": "1GB",
                    "eviction_policy": "allkeys-lru",
                    "endpoint_protocol": "redis",
                    "endpoint_port": 6379,
                    "host": "cache_container_runtime"
                }
            },

            "container_runtimes": {
                "frontend_container_runtime": {
                    "type": "tosca.nodes.Container.Runtime.Docker",
                    "num_cpus": 2,
                    "mem_size_gb": 4
                },
                "backend_container_runtime": {
                    "type": "tosca.nodes.Container.Runtime.Docker",
                    "num_cpus": 4,
                    "mem_size_gb": 8
                },
                "database_container_runtime": {
                    "type": "tosca.nodes.Container.Runtime.Docker",
                    "num_cpus": 4,
                    "mem_size_gb": 16,
                    "disk_size_gb": 100
                },
                "cache_container_runtime": {
                    "type": "tosca.nodes.Container.Runtime.Docker",
                    "num_cpus": 2,
                    "mem_size_gb": 2
                }
            },

            "resource_totals": {
                "total_cpu_cores": 12,
                "total_memory_gb": 30,
                "total_disk_gb": 100
            },

            "deployment_tiers": ["frontend", "backend", "data"],

            "groups": {
                "frontend_tier": ["web_frontend"],
                "backend_tier": ["api_gateway", "redis_cache"],
                "data_tier": ["postgres_db"]
            },

            "policies": {
                "scaling": {
                    "enabled": true,
                    "targets": ["frontend_tier", "backend_tier"],
                    "min_instances": 2,
                    "max_instances": 10,
                    "default_instances": 3
                },
                "placement": {
                    "enabled": true,
                    "targets": ["data_tier"],
                    "constraint": "region=eu-central",
                    "anti_affinity": true,
                    "placement_region": "eu-central"
                },
                "monitoring": {
                    "enabled": true,
                    "targets": ["web_frontend", "api_gateway", "postgres_db"],
                    "interval": "30s",
                    "metrics": ["cpu_utilization", "memory_usage", "response_time", "error_rate"]
                }
            },

            "technology_stack": ["Docker", "PostgreSQL", "Redis", "Node.js"],

            "node_types": [
                "tosca.nodes.Container.Application.Docker",
                "tosca.nodes.Database.PostgreSQL",
                "tosca.nodes.Database.Redis",
                "tosca.nodes.Container.Runtime.Docker"
            ],

            "endpoints": {
                "application_endpoint": "http://web_frontend:80/",
                "api_endpoint": "http://api_gateway:8080/api"
            },

            "imports": [
                "https://swarmchestrate.eu/tosca/types/nodes.yaml",
                "https://swarmchestrate.eu/tosca/types/relationships.yaml"
            ],

            "created_at": "2025-11-05T10:30:00Z",
            "updated_at": "auto-calculate",
            "kbagent": "swarmchestrate-orchestrator",
            "kbagent_type": "TOSCA"
        }
    ]
}

